<?phpclass Layout	{				public $head;		public $header;		public $footer;				function __construct($data,$head='head', $header='header', $footer='footer')			{				$this->data = $data;				$this->head = $head;				$this->header = $header;				$this->footer = $footer;			}					//public function head($data,$extensions=0)		public function head($extensions=0)			{				$data = extract($this->data);				include_once 'application/layout/' . $this->head  . '.php';								if($extensions){					$append = '';					foreach($extensions as $extension){						$append.=$extension;					}					echo $append;				}			}		//public function header($data, $special=0)		public function header($special=0)			{				$data = extract($this->data);				if($special){					include_once 'application/layout/' . $special  . '.php';				} else{					include_once 'application/layout/' . $this->header  . '.php';									}							}		//public function footer($data, $special=0)		public function footer($special=0)			{				$data = extract($this->data);				if($special){					include_once 'application/layout/' . $special  . '.php';				} else{					include_once 'application/layout/' . $this->footer  . '.php';				}							}	}	?>